// producer.js
const { Kafka } = require('kafkajs');

async function produce() {
    const kafka = new Kafka({
        clientId: 'my-node-app',
        brokers: ['localhost:9092'], // Connect to local Kafka broker
    });

    const producer = kafka.producer();

    try {
        // Connect to the producer
        await producer.connect();
        console.log('Producer connected!');

        // Send a message to Kafka topic 'my-topic'
        await producer.send({
            topic: 'my-first-topic', // Replace with your topic
            messages: [
                { value: 'Hello World, This is Kafkajs from Nodejs!' }, // Your message content
            ],
        });

        console.log('Message sent to Kafka topic!');
    } catch (error) {
        console.error('Error producing message:', error);
    } finally {
        // Disconnect the producer
        await producer.disconnect();
    }
}

produce().catch(console.error);
